import { MenuItem, Select, InputLabel, FormControl, TextField, Box, Paper, Typography, Button, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Grid, Dialog, DialogTitle, DialogContent, IconButton } from '@mui/material';
import { Description, Edit, Delete } from '@mui/icons-material';
import CloseIcon from '@mui/icons-material/Close';
import Sidebar from './Sidebar';
import React, { useState, useEffect } from 'react';
import AddIcon from '@mui/icons-material/Add';

const Myactivity = () => {
  const [requests, setRequests] = useState([]);  // เก็บข้อมูลคำร้องทั้งหมด
  const [selectedEvent, setSelectedEvent] = useState(null); 
  const [dialogType, setDialogType] = useState(''); // ใช้จัดการประเภทของ dialog ที่เปิด

  const users = [
    { user_id: 1, name: 'John Doe', tel: '0812345678' },
    { user_id: 2, name: 'Jane Smith', tel: '0897654321' },
  ];

  const [formData, setFormData] = useState({
    event_name: '',
    type: '',
    address: '',
    province: '',
    detail: '',
    amount: '',
    startdate: '',
    enddate: '',
    timestart: '',
    date_create: new Date().toISOString().slice(0, 16),
    user: '',
  });
  
  const [eventImg, setEventImg] = useState(null);

  const provinces = [
    { value: 'กรุงเทพมหานคร', label: 'กรุงเทพมหานคร' },
    { value: 'กระบี่', label: 'กระบี่' },
    { value: 'กาญจนบุรี', label: 'กาญจนบุรี' },
    // เพิ่มรายการจังหวัดที่เหลือ...
  ];
  
  const eventTypes = [
    { value: 'ผู้พิการ', label: 'ผู้พิการ' },
    { value: 'การศึกษาและเยาวชน', label: 'การศึกษาและเยาวชน' },
    // เพิ่มรายการหมวดหมู่ที่เหลือ...
  ];

  // ฟังก์ชัน handleOpen
  const handleOpen = (type, event = null) => {
    setDialogType(type); // กำหนดประเภท dialog ที่จะเปิด ('form', 'details', 'edit')
    if (event) setSelectedEvent(event); // กรณีที่มีข้อมูลกิจกรรม ให้เก็บไว้ใน state
  };

  // ฟังก์ชัน handleClose
  const handleClose = () => {
    setDialogType(''); // ตั้งค่า dialogType เป็นค่าว่าง เพื่อปิดโมดาล
    setSelectedEvent(null); // ล้างข้อมูลกิจกรรม
  };

  const handleFileChange = (e) => setEventImg(e.target.files[0]);

  const handleSubmit = () => {
    const isFormValid = Object.values(formData).every(field => field !== '');
    if (!isFormValid) {
      alert('กรุณากรอกข้อมูลให้ครบทุกช่อง');
      return;
    }
    const data = new FormData();
    Object.keys(formData).forEach((key) => data.append(key, formData[key]));
    if (eventImg) data.append('event_img', eventImg);

    fetch('http://127.0.0.1:8000/api/events/', { method: 'POST', body: data })
      .then((response) => response.json().then((data) => ({ status: response.status, data })))
      .then(({ status, data }) => {
        if (status === 400) console.error('Bad Request:', data);
        else console.log('Event created:', data);
        handleClose(); // ปิดฟอร์มหลังจากสร้างกิจกรรมเสร็จสิ้น
      })
      .catch((error) => console.error('Error creating event:', error));
  };

  const deleteEventById = (id) => {
    const confirmDelete = window.confirm(`คุณแน่ใจหรือไม่ว่าต้องการลบกิจกรรม?`);
    if (confirmDelete) {
      fetch(`http://127.0.0.1:8000/api/events/${id}/`, { method: 'DELETE' })
        .then(() => {
          setRequests(requests.filter(request => request.event.event_id !== id));
        })
        .catch((error) => console.error('Error deleting event:', error));
    }
  };

  useEffect(() => {
    fetch('http://127.0.0.1:8000/api/requests/')
      .then((response) => response.json())
      .then((data) => setRequests(data))
      .catch((error) => console.error('Error fetching requests:', error));
  }, []);

  const renderTable = (title, status) => {
    const filteredRequests = requests.filter((request) => request.status === status);

    if (filteredRequests.length === 0) return null;

    return (
      <Box p={3}>
        <Typography variant="h6" mt={3}>{title}</Typography>
        <TableContainer component={Paper}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>ชื่อกิจกรรม</TableCell>
                <TableCell>ประเภท</TableCell>
                <TableCell>วันจัดกิจกรรม</TableCell>
                <TableCell>จำนวนคนลงทะเบียน</TableCell>
                <TableCell>การจัดการ</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {filteredRequests.map((request) => (
                <TableRow key={request.request_id}>
                  <TableCell>{request.event.event_name}</TableCell>
                  <TableCell>{request.event.type}</TableCell>
                  <TableCell>{`${request.event.startdate} - ${request.event.enddate}`}</TableCell>
                  <TableCell>{request.event.amount}</TableCell>
                  <TableCell>
                    <Description style={{ cursor: 'pointer' }} onClick={() => handleOpen('details', request.event)} />
                    <Edit style={{ cursor: 'pointer' }} onClick={() => handleOpen('edit', request.event)} />
                    <Delete style={{ cursor: 'pointer' }} onClick={() => deleteEventById(request.event.event_id)} />
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Box>
    );
  };

  return (
    <Box sx={{ display: 'flex', padding: 2 }}>
      <Sidebar />
      <Box flexGrow={1} bgcolor="#f5f5f5" p={3}>
        <Paper elevation={2} sx={{ padding: 3 }}>
          <Button variant="contained" color="primary" onClick={() => handleOpen('form')} startIcon={<AddIcon />}>
            เพิ่มกิจกรรม
          </Button>

          {/* Dialog สำหรับสร้างกิจกรรม */}
          <Dialog open={dialogType === 'form'} onClose={handleClose} maxWidth="md" fullWidth>
            <DialogTitle>
              สร้างกิจกรรม
              <IconButton aria-label="close" onClick={handleClose} sx={{ position: 'absolute', right: 8, top: 8, color: (theme) => theme.palette.grey[500] }}>
                <CloseIcon />
              </IconButton>
            </DialogTitle>
            <DialogContent>
              {/* ฟอร์มสำหรับสร้างกิจกรรม */}
            </DialogContent>
          </Dialog>

          {/* Dialog สำหรับแสดงรายละเอียดกิจกรรม */}
          <Dialog open={dialogType === 'details'} onClose={handleClose} maxWidth="md" fullWidth>
            <DialogTitle>
              รายละเอียดกิจกรรม
              <IconButton aria-label="close" onClick={handleClose} sx={{ position: 'absolute', right: 8, top: 8, color: (theme) => theme.palette.grey[500] }}>
                <CloseIcon />
              </IconButton>
            </DialogTitle>
            <DialogContent>
              {selectedEvent ? (
                <Box>
                  <Typography variant="h6">{selectedEvent.event_name}</Typography>
                  <Typography variant="body1">หมวดหมู่: {selectedEvent.type}</Typography>
                  <Typography variant="body1">สถานที่: {selectedEvent.address}, {selectedEvent.province}</Typography>
                  <Typography variant="body1">รายละเอียด: {selectedEvent.detail}</Typography>
                  <Typography variant="body1">จำนวนคนลงทะเบียน: {selectedEvent.amount}</Typography>
                  <Typography variant="body1">วันที่จัด: {selectedEvent.startdate} - {selectedEvent.enddate}</Typography>
                  <Typography variant="body1">เวลาจัด: {selectedEvent.timestart}</Typography>
                </Box>
              ) : (
                <Typography variant="body1">กำลังโหลดข้อมูล...</Typography>
              )}
            </DialogContent>
          </Dialog>

          {/* Dialog สำหรับแก้ไขกิจกรรม */}
          <Dialog open={dialogType === 'edit'} onClose={handleClose} maxWidth="md" fullWidth>
            <DialogTitle>
              แก้ไขกิจกรรม
              <IconButton aria-label="close" onClick={handleClose} sx={{ position: 'absolute', right: 8, top: 8, color: (theme) => theme.palette.grey[500] }}>
                <CloseIcon />
              </IconButton>
            </DialogTitle>
            <DialogContent>
              {selectedEvent ? (
                <Box>
                  <TextField label="ชื่อกิจกรรม" name="event_name" fullWidth value={selectedEvent.event_name} onChange={(e) => setSelectedEvent({ ...selectedEvent, event_name: e.target.value })} />
                  {/* ฟอร์มแก้ไขกิจกรรม */}
                </Box>
              ) : (
                <Typography variant="body1">กำลังโหลดข้อมูล...</Typography>
              )}
            </DialogContent>
          </Dialog>

          {/* Render ตาราง */}
          {renderTable('อนุมัติแล้ว', 'อนุมัติ')}
          {renderTable('รออนุมัติ', 'รออนุมัติ')}
          {renderTable('ไม่อนุมัติ', 'ไม่อนุมัติ')}
        </Paper>
      </Box>
    </Box>
  );
};

export default Myactivity;
