  const deleteEventById = (id) => {
    // แจ้งเตือนเพื่อให้ผู้ใช้ยืนยันการลบ
    const confirmDelete = confirm(`คุณแน่ใจหรือไม่ว่าต้องการลบกิจกรรมที่มี ID ${id}?`);
    
    if (confirmDelete) {
      fetch(`http://127.0.0.1:8000/api/events/${id}/`, { method: 'DELETE' })
        .then((response) => {
          if (response.ok) {
            alert(`กิจกรรมที่มี ID ${id} ถูกลบเรียบร้อยแล้ว`);
            window.location.reload();
          } else {
            alert(`เกิดข้อผิดพลาดในการลบกิจกรรมที่มี ID ${id}`);
          }
        })
        .catch((error) => console.error('Error deleting event:', error));
    } else {
      alert('การลบกิจกรรมถูกยกเลิก');
    }
  };
  

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: name === 'timestart' ? `${value}:00` : value });
  };

  const handleSubmit = () => {
    const isFormValid = Object.values(formData).every(field => field !== '');
    if (!isFormValid) {
      alert('กรุณากรอกข้อมูลให้ครบทุกช่อง');
      return;
    }
    const data = new FormData();
    Object.keys(formData).forEach((key) => data.append(key, formData[key]));
    if (eventImg) data.append('event_img', eventImg);

    fetch('http://127.0.0.1:8000/api/events/', { method: 'POST', body: data })
      .then((response) => response.json().then((data) => ({ status: response.status, data })))
      .then(({ status, data }) => {
        if (status === 400) console.error('Bad Request:', data);
        else console.log('Event created:', data);
        handleCloseForm();
      })
      .catch((error) => console.error('Error creating event:', error));
  };

  useEffect(() => {
    fetch('http://127.0.0.1:8000/api/requests/')
      .then((response) => response.json())
      .then((data) => setRequests(data))
      .catch((error) => console.error('Error fetching requests:', error));
  }, []);

  const renderTable = (title, status) => {
    const filteredRequests = requests.filter((request) => request.status === status);

    if (filteredRequests.length === 0) return null;

    return (
      <Box p={3}>
        <Typography variant="h6" mt={3}>{title}</Typography>
        <TableContainer component={Paper}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>ชื่อกิจกรรม</TableCell>
                <TableCell>ประเภท</TableCell>
                <TableCell>วันจัดกิจกรรม</TableCell>
                <TableCell>จำนวนคนลงทะเบียน</TableCell>
                <TableCell> * </TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {filteredRequests.map((request) => (
                <TableRow key={request.request_id}>
                  <TableCell>{request.event.event_name}</TableCell>
                  <TableCell>{request.event.type}</TableCell>
                  <TableCell>{`${request.event.startdate} - ${request.event.enddate}`}</TableCell>
                  <TableCell>{request.event.amount}</TableCell>
                  <TableCell>
                    <Description style={{ cursor: 'pointer' }} />
                    <Edit style={{ cursor: 'pointer' }} />
                    <Delete style={{ cursor: 'pointer' }} onClick={() => deleteEventById(request.event.event_id)} />
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Box>
    );
  };

  return (
    <Box sx={{ display: 'flex', padding: 2 }}>
      <Sidebar />
      <Box flexGrow={1} bgcolor="#f5f5f5" p={3}>
        <Paper elevation={2} sx={{ padding: 3 }}>
          <Paper elevation={3} sx={{ padding: 3, marginBottom: 4, borderRadius: '50' }}>
            <Typography variant="h5" gutterBottom>โปรไฟล์</Typography>
            <Grid container spacing={2}>
              <Grid item xs={12} md={6}>
                <Typography variant="body1">อาสาบ้านดินไทย</Typography>
                <Typography variant="body1">Tel: 369 258 147</Typography>
                <Typography variant="body1">Email: guyhawkins@gmail.com</Typography>
              </Grid>
            </Grid>
          </Paper>

          <Button variant="contained" color="primary" onClick={handleOpenForm} startIcon={<AddIcon />}>
            เพิ่มกิจกรรม
          </Button>

  {/* Form for Adding Event */}
  <Dialog open={open} onClose={handleCloseForm} maxWidth="md" fullWidth>
      <DialogTitle>
          สร้างกิจกรรม
          <IconButton
            aria-label="close"
            onClick={handleCloseForm}
            sx={{
              position: 'absolute',
              right: 8,
              top: 8,
              color: (theme) => theme.palette.grey[500],
            }}
          >
            <CloseIcon />
          </IconButton>
      </DialogTitle>
        <DialogContent>
          <Box sx={{ padding: 2 }}>
            <Grid container spacing={2}>
              {/* ชื่อกิจกรรม */}
              <Grid item xs={12} md={6}>
                <TextField label="ชื่อกิจกรรม" name="event_name" fullWidth onChange={handleChange}  value={formData.event_name}/>
              </Grid>

             {/* หมวดหมู่ */}
             <Grid item xs={12} md={6}>
              <FormControl fullWidth>
                <InputLabel>หมวดหมู่</InputLabel>
                <Select
                  label="หมวดหมู่"
                  name="type"
                  value={formData.type}
                  onChange={handleChange}
                >
                  {eventTypes.map((eventType) => (
                    <MenuItem key={eventType.value} value={eventType.value}>
                      {eventType.label}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>

            {/* สถานที่จัด */}
            <Grid item xs={12} md={6}>
              <TextField
                label="สถานที่จัด"
                name="address"
                fullWidth
                onChange={handleChange}
                value={formData.address}
              />
            </Grid>

            {/* จังหวัด */}
            <Grid item xs={12} md={6}>
              <FormControl fullWidth>
                <InputLabel>จังหวัด</InputLabel>
                <Select
                  label="จังหวัด"
                  name="province"
                  value={formData.province}
                  onChange={handleChange}
                >
                  {provinces.map((province) => (
                    <MenuItem key={province.value} value={province.value}>
                      {province.label}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>
                                    
            {/* รายละเอียด */}
            <Grid item xs={12}>
              <TextField
                label="รายละเอียด"
                name="detail"
                fullWidth
                multiline
                rows={4}
                onChange={handleChange}
                value={formData.detail}
              />
            </Grid>

            {/* จำนวนที่รับ */}
            <Grid item xs={12} md={6}>
              <TextField
                label="จำนวนที่รับ"
                name="amount"
                fullWidth
                onChange={handleChange}
                type="number"
                value={formData.amount}
              />
            </Grid>

            {/* วันที่เริ่มกิจกรรม */}
            <Grid item xs={12} md={6}>
              <TextField
                label="วันที่เริ่มกิจกรรม"
                name="startdate"
                type="date"
                fullWidth
                onChange={handleChange}
                value={formData.startdate}
                InputLabelProps={{
                  shrink: true,
                }}
              />
            </Grid>

            {/* วันสิ้นสุดกิจกรรม */}
            <Grid item xs={12} md={6}>
              <TextField
                label="วันสิ้นสุดกิจกรรม"
                name="enddate"
                type="date"
                fullWidth
                onChange={handleChange}
                value={formData.enddate}
                InputLabelProps={{
                  shrink: true,
                }}
              />
            </Grid>

            {/* เวลาจัดกิจกรรม */}
            <Grid item xs={12} md={6}>
              <TextField
                label="เวลาจัดกิจกรรม"
                name="timestart"
                type="time"
                fullWidth
                onChange={handleChange}  // เรียกใช้ handleChange ที่อัปเดตแล้ว
                value={formData.timestart.slice(0, 5)} // แสดงแค่ HH:MM ใน input แต่เก็บ HH:MM:SS
                InputLabelProps={{
                  shrink: true,
                }}
              />
            </Grid>

          </Grid>
          {/* วันที่สร้างกิจกรรม */}
          <Grid item xs={12} md={6}>
            <TextField
              label="วันที่สร้างกิจกรรม"
              name="date_create"
              type="datetime-local"  // รูปแบบ datetime-local สำหรับดึงวันที่และเวลา
              fullWidth
              onChange={handleChange}  // อัปเดตค่าเมื่อมีการเปลี่ยนแปลง
              value={formData.date_create}  // เก็บค่าของ date_create ใน formData
              InputLabelProps={{
                shrink: true,  // ทำให้ label ยังคงอยู่เมื่อมีค่าในช่อง TextField
              }}
            />
          </Grid>
            {/* รายชื่อผู้จัดกิจกรรม */}
            <Grid item xs={12} md={6}>
              <FormControl fullWidth>
                <InputLabel>ผู้จัดกิจกรรม</InputLabel>
                <Select
                  label="ผู้จัดกิจกรรม"
                  name="user"
                  value={formData.user}
                  onChange={handleChange} // เมื่อเลือกผู้ใช้ จะอัปเดตค่า user_id ใน formData
                >
                  {users.map((user) => (
                    <MenuItem key={user.user_id} value={user.user_id}>
                      {user.name} ({user.tel}) {/* แสดงชื่อและเบอร์โทรของผู้ใช้ */}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>

                  {/* อัปโหลดไฟล์รูปภาพ */}
                  <Grid item xs={12} md={6}>
                    <input
                      accept="image/*"
                      type="file"
                      onChange={handleFileChange}
                    />
                  </Grid>
              
          <Box mt={3} display="flex" justifyContent="flex-end">
            <Button variant="contained" color="primary" onClick={handleSubmit}>
              สร้างกิจกรรม
            </Button>
          </Box>
        </Box>
      </DialogContent>
    </Dialog>

          {/* Tables for Approved, Pending, and Rejected Activities */}
          {renderTable('อนุมัติแล้ว', 'อนุมัติ')}
          {renderTable('รออนุมัติ', 'รออนุมัติ')}
          {renderTable('ไม่อนุมัติ', 'ไม่อนุมัติ')}
        </Paper>
      </Box>
    </Box>
  );
};

export default Myactivity;
