import Sidebar from './Sidebar';
import React, { useState, useEffect } from 'react';
import { Box, TextField, Typography, List, ListItem, ListItemText, Pagination } from '@mui/material';
import { useLocation } from 'react-router-dom';

const ListRegis = () => {
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  const eventID = searchParams.get('eventID'); // Extract eventID from query parameters
  const [registers, setRegisters] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredRegisters, setFilteredRegisters] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 10; // Set the number of items per page

  useEffect(() => {
    if (eventID) {
      console.log('Event ID:', eventID);
  
      fetch(`http://127.0.0.1:8000/api/registers/?event=${eventID}`)
        .then((response) => {
          if (!response.ok) {
            throw new Error('Failed to fetch registration data');
          }
          return response.json();
        })
        .then((data) => {
          console.log('Registrations for eventID', eventID, ':', data);
          setRegisters(data);
          setFilteredRegisters(data);
          setLoading(false);
        })
        .catch((error) => {
          console.error('Error fetching registration data:', error);
          setError('Failed to load registration data. Please try again later.');
          setLoading(false);
        });
    } else {
      console.log('No eventID provided.');
    }
  }, [eventID]);
  
  // Handle search input change
  const handleSearch = (e) => {
    const query = e.target.value.toLowerCase();
    setSearchQuery(query);
    const filtered = registers.filter((reg) =>
      reg.name.toLowerCase().includes(query)
    );
    setFilteredRegisters(filtered);
    setCurrentPage(1); // Reset to the first page when search changes
  };

  // Pagination logic
  const handlePageChange = (event, value) => {
    setCurrentPage(value);
  };

  const displayedRegisters = filteredRegisters.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  return (
    <Box sx={{ display: 'flex', padding: 2 }}>
      {/* Sidebar */}
      <Sidebar />

      {/* Main content */}
      <Box sx={{ flex: 1, marginLeft: 2 }}>
        <Typography variant="h5">
          รายชื่อคนลงทะเบียน
        </Typography>

        <Box sx={{ marginY: 2 }}>
          <TextField
            fullWidth
            variant="outlined"
            placeholder="ค้นหา..."
            value={searchQuery}
            onChange={handleSearch}
          />
        </Box>

        {loading ? (
          <Typography>Loading...</Typography>
        ) : error ? (
          <Typography color="error">{error}</Typography>
        ) : (
          <>
            <List>
              {displayedRegisters.map((reg) => (
                <ListItem key={reg.id}>
                  <ListItemText
                    primary={reg.name}
                    secondary={`Email: ${reg.email} | Phone: ${reg.phone}`}
                  />
                </ListItem>
              ))}
            </List>
            <Box sx={{ display: 'flex', justifyContent: 'center', marginTop: 2 }}>
              <Pagination
                count={Math.ceil(filteredRegisters.length / itemsPerPage)}
                page={currentPage}
                onChange={handlePageChange}
                variant="outlined"
                shape="rounded"
              />
            </Box>
          </>
        )}
      </Box>
    </Box>
  );
};

export default ListRegis;
