              onChange={handleChange}  // อัปเดตค่าเมื่อมีการเปลี่ยนแปลง
              value={formData.date_create}  // เก็บค่าของ date_create ใน formData
              InputLabelProps={{
                shrink: true,  // ทำให้ label ยังคงอยู่เมื่อมีค่าในช่อง TextField
              }}
            />
          </Grid>
          {/* รายชื่อผู้จัดกิจกรรม */}
          <Grid item xs={12} md={6}>
            <TextField
              label="ผู้จัดกิจกรรม"
              fullWidth
              value={UserData ? `${UserData.name} (${UserData.tel})` : ''} // ตรวจสอบว่า UserData มีค่าหรือไม่ก่อนแสดง
              InputProps={{
                readOnly: true, // ทำให้ไม่สามารถแก้ไขข้อมูลใน TextField ได้
              }}
            />
          </Grid>
                  {/* อัปโหลดไฟล์รูปภาพ */}
                  <Grid item xs={12} md={6}>
                    <input
                      accept="image/*"
                      type="file"
                      onChange={handleFileChange}
                    />
                  </Grid>
              
          <Box mt={3} display="flex" justifyContent="flex-end">
            <Button variant="contained" color="primary" onClick={handleSubmit}>
              สร้างกิจกรรม
            </Button>
          </Box>
        </Box>
      </DialogContent>
    </Dialog>
    {/* The Dialog for showing details* */}
    <Dialog open={openDetails} onClose={handleCloseDetails} maxWidth="md" fullWidth>
  <DialogTitle>
    รายละเอียดกิจกรรม
    <IconButton
      aria-label="close"
      onClick={handleCloseDetails}
      sx={{
        position: 'absolute',
        right: 8,
        top: 8,
        color: (theme) => theme.palette.grey[500],
      }}
    >
      <CloseIcon />
    </IconButton>
  </DialogTitle>
  <DialogContent>
    {selectedEvent && (
      <Box>
        <Typography variant="h6">{selectedEvent.event_name}</Typography>
        <Typography variant="body1">หมวดหมู่: {selectedEvent.type}</Typography>
        <Typography variant="body1">สถานที่: {selectedEvent.address}, {selectedEvent.province}</Typography>
        <Typography variant="body1">รายละเอียด: {selectedEvent.detail}</Typography>
        <Typography variant="body1">จำนวนคนลงทะเบียน: {selectedEvent.amount}</Typography>
        <Typography variant="body1">วันที่จัด: {selectedEvent.startdate} - {selectedEvent.enddate}</Typography>
        <Typography variant="body1">เวลาจัด: {selectedEvent.timestart}</Typography>
        {selectedEvent.event_img && (
          <img
            src={URL.createObjectURL(selectedEvent.event_img)}
            alt="Event"
            style={{ width: '100%', height: 'auto', marginBottom: '20px' }}
          />
        )}
      </Box>
    )}
  </DialogContent>
</Dialog>

          {/* Tables for Approved, Pending, and Rejected Activities */}
          {renderTable('อนุมัติแล้ว', 'อนุมัติ')}
          {renderTable('รออนุมัติ', 'รออนุมัติ')}
          {renderTable('ไม่อนุมัติ', 'ไม่อนุมัติ')}
        </Paper>
      </Box>
    </Box>
  );
};

export default Myactivity;
