const renderEditForm = () => {
  if (!editEventData) return null;
  const { requestStatus } = editEventData;
{/* Dialog for edit details */}
  return (
    <Dialog open={openEditForm} onClose={handleCloseEditForm} maxWidth="md" fullWidth>
      <DialogTitle>
        แก้ไขกิจกรรม 
        <IconButton
          aria-label="close"
          onClick={handleCloseEditForm}
          sx={{
            position: 'absolute',
            right: 8,
            top: 8,
            color: (theme) => theme.palette.grey[500],
          }}
        >
          <CloseIcon />
        </IconButton>
      </DialogTitle>
      <DialogContent>
        <Box sx={{ padding: 2 }}>
          <Grid container spacing={2}>
            {/* แสดงฟิลด์ตามสถานะคำร้อง */}
            {requestStatus === 'อนุมัติ' ? (
              <>
                <Grid item xs={12} md={6}>
                  <TextField
                    label="วันที่เริ่มกิจกรรม"
                    name="startdate"
                    type="date"
                    fullWidth
                    onChange={handleChange}
                    value={formData.startdate}
                    InputLabelProps={{ shrink: true }}
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    label="วันสิ้นสุดกิจกรรม"
                    name="enddate"
                    type="date"
                    fullWidth
                    onChange={handleChange}
                    value={formData.enddate}
                    InputLabelProps={{ shrink: true }}
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    label="จำนวนที่รับ"
                    name="amount"
                    fullWidth
                    onChange={handleChange}
                    type="number"
                    value={formData.amount}
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    label="เวลาจัดกิจกรรม"
                    name="timestart"
                    type="time"
                    fullWidth
                    onChange={handleChange}
                    value={formData.timestart.slice(0, 5)}
                    InputLabelProps={{ shrink: true }}
                  />
                </Grid>
              </>
            ) : (
              <>
                <Grid item xs={12} md={6}>
                  <TextField
                    label="ชื่อกิจกรรม"
                    name="event_name"
                    fullWidth
                    onChange={handleChange}
                    value={formData.event_name}
                  />
                </Grid>
                {/* หมวดหมู่ */}
             <Grid item xs={12} md={6}>
              <FormControl fullWidth>
                <InputLabel>หมวดหมู่</InputLabel>
                <Select
                  label="หมวดหมู่"
                  name="type"
                  value={formData.type}
                  onChange={handleChange}
                >
                  {eventTypes.map((eventType) => (
                    <MenuItem key={eventType.value} value={eventType.value}>
                      {eventType.label}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>

            {/* สถานที่จัด */}
            <Grid item xs={12} md={6}>
              <TextField
                label="สถานที่จัด"
                name="address"
                fullWidth
                onChange={handleChange}
                value={formData.address}
              />
            </Grid>

            {/* จังหวัด */}
            <Grid item xs={12} md={6}>
              <FormControl fullWidth>
                <InputLabel>จังหวัด</InputLabel>
                <Select
                  label="จังหวัด"
                  name="province"
                  value={formData.province}
                  onChange={handleChange}
                >
                  {provinces.map((province) => (
                    <MenuItem key={province.value} value={province.value}>
                      {province.label}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>
                                    
            {/* รายละเอียด */}
            <Grid item xs={12}>
              <TextField
                label="รายละเอียด"
                name="detail"
                fullWidth
                multiline
                rows={4}
                onChange={handleChange}
                value={formData.detail}
              />
            </Grid>

            {/* จำนวนที่รับ */}
            <Grid item xs={12} md={6}>
              <TextField
                label="จำนวนที่รับ"
                name="amount"
                fullWidth
                onChange={handleChange}
                type="number"
                value={formData.amount}
              />
            </Grid>

            {/* วันที่เริ่มกิจกรรม */}
            <Grid item xs={12} md={6}>
              <TextField
                label="วันที่เริ่มกิจกรรม"
                name="startdate"
                type="date"
                fullWidth
                onChange={handleChange}
                value={formData.startdate}
                InputLabelProps={{
                  shrink: true,
                }}
              />
            </Grid>

            {/* วันสิ้นสุดกิจกรรม */}
            <Grid item xs={12} md={6}>
              <TextField
                label="วันสิ้นสุดกิจกรรม"
                name="enddate"
                type="date"
                fullWidth
                onChange={handleChange}
                value={formData.enddate}
                InputLabelProps={{
                  shrink: true,
                }}
              />
            </Grid>

            {/* เวลาจัดกิจกรรม */}
            <Grid item xs={12} md={6}>
              <TextField
                label="เวลาจัดกิจกรรม"
                name="timestart"
                type="time"
                fullWidth
                onChange={handleChange}  // เรียกใช้ handleChange ที่อัปเดตแล้ว
                value={formData.timestart.slice(0, 5)} // แสดงแค่ HH:MM ใน input แต่เก็บ HH:MM:SS
                InputLabelProps={{
                  shrink: true,
                }}
              />
            </Grid>
          {/* วันที่สร้างกิจกรรม */}
          <Grid item xs={12} md={6}>
          <TextField
            label="วันที่สร้างกิจกรรม"
            name="date_create"
            type="datetime-local"  // รูปแบบ datetime-local สำหรับดึงวันที่และเวลา
            fullWidth
            value={formData.date_create}  // เก็บค่าของ date_create ใน formData
            InputProps={{
              readOnly: true,  // ทำให้ไม่สามารถแก้ไขข้อมูลใน TextField ได้
            }}
            InputLabelProps={{
              shrink: true,  // ทำให้ label ยังคงอยู่เมื่อมีค่าในช่อง TextField
            }}
          />
        </Grid>
          {/* รายชื่อผู้จัดกิจกรรม */}
          <Grid item xs={12} md={6}>
            <TextField
              label="ผู้จัดกิจกรรม"
              fullWidth
              value={UserData ? `${UserData.name} (${UserData.tel})` : ''} // ตรวจสอบว่า UserData มีค่าหรือไม่ก่อนแสดง
              InputProps={{
               
              }}
            />
          </Grid>
                  {/* อัปโหลดไฟล์รูปภาพ */}
                  <Grid item xs={12} md={6}>
                    <input
                      accept="image/*"
                      type="file"
                      onChange={handleFileChange}
                    />
                  </Grid>
              </>
            )}
          </Grid>
          <Box mt={3} display="flex" justifyContent="flex-end">
            <Button variant="contained" color="primary" onClick={handleUpdate}>
              แก้ไขกิจกรรม
            </Button>
          </Box>
        </Box>
      </DialogContent>
    </Dialog>
  );
};
{/* showTable */}
const renderTable = (title, status) => {
  const filteredRequests = requests.filter((request) => 
    request.status === status &&
    (request.event.event_name.toLowerCase().includes(searchTerm.toLowerCase()) || 
     request.event.type.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  if (filteredRequests.length === 0) {
    return null;
  }
  let statusColor = '#999999'; // Default color
  if (status === 'อนุมัติ') statusColor = '#2EBDBD';
  else if (status === 'รออนุมัติ') statusColor = '#FFCC33';
  else if (status === 'ไม่อนุมัติ') statusColor = '#CC0000';
  return (
<Box p={3}>
      <Typography
        variant="h6"
        mt={3}
        sx={{
          backgroundColor: statusColor,
          color: 'white',
          borderRadius: '16px', // ทำให้กรอบมีขอบโค้ง
          padding: '8px 16px', // เพิ่มช่องว่างด้านในเพื่อให้ข้อความดูดีขึ้น
          display: 'inline-block', // ทำให้ขนาดพอดีกับข้อความ
          width: '10%',
          marginBottom: '16px', // เพิ่มระยะห่างด้านล่างจากตาราง
          textAlign: 'center',
        }}
      >
        {title}
      </Typography>
      <TableContainer component={Paper}>
  <Table>
    <TableHead>
      <TableRow>
        <TableCell
          align="center"
          sx={{ backgroundColor: 'gray', color: 'white', width: '20%' }}
        >
          ชื่อกิจกรรม
        </TableCell>
        <TableCell
          align="center"
          sx={{ backgroundColor: 'gray', color: 'white', width: '20%' }}
        >
          ประเภท
        </TableCell>
        <TableCell
          align="center"
          sx={{ backgroundColor: 'gray', color: 'white', width: '20%' }}
        >
          วันจัดกิจกรรม
        </TableCell>
        <TableCell
          align="center"
          sx={{ backgroundColor: 'gray', color: 'white', width: '20%' }}
        >
          จำนวนคนลงทะเบียน
        </TableCell>
        <TableCell
          align="center"
          sx={{ backgroundColor: 'gray', color: 'white', width: '20%' }}
        >
          *
        </TableCell>
      </TableRow>
    </TableHead>
    <TableBody>
      {filteredRequests.map((request) => {
        const registrationCount = countRegistrationsForEvent(request.event.event_id);
        return (
          <TableRow key={request.request_id}>
            <TableCell align="center" sx={{ width: '20%' }}>
              {request.event.event_name}
            </TableCell>
            <TableCell align="center" sx={{ width: '20%' }}>
              {request.event.type}
            </TableCell>
            <TableCell align="center" sx={{ width: '20%' }}>
              {`${request.event.startdate} - ${request.event.enddate}`}
            </TableCell>
            <TableCell align="center" sx={{ width: '20%' }}>
              {registrationCount}/{request.event.amount}
              {status === 'อนุมัติ' && (
                <span
                  onClick={() => listRegister(request.event.event_id)}
                  style={{ color: 'blue', textDecoration: 'none', cursor: 'pointer', marginLeft: '8px' }}
                >
                  รายชื่อ
                </span>
              )}
            </TableCell>
            <TableCell align="center" sx={{ width: '20%' }}>
              <Box
                sx={{
                  display: 'flex',
                  justifyContent: 'center',
                  gap: 1,
                }}
              >
                <IconButton
                  sx={{
                    backgroundColor: '#E0E0E0',
                    borderRadius: '8px',
                    padding: '8px',
                    '&:hover': {
                      backgroundColor: '#D3D3D3',
                    },
                  }}
                  onClick={() => handleOpenDetailDialog(request.event.event_id)}
                >
                  <DescriptionIcon sx={{ color: 'blue' }} />
                </IconButton>

                {status !== 'ไม่อนุมัติ' && (
                  <IconButton
                    sx={{
                      backgroundColor: '#E0E0E0',
                      borderRadius: '8px',
                      padding: '8px',
                      '&:hover': {
                        backgroundColor: '#D3D3D3',
                      },
                    }}
                    onClick={() => handleOpenEditForm(request.event, request)}
                  >
                    <EditIcon sx={{ color: 'orange' }} />
                  </IconButton>
                )}

                <IconButton
                  sx={{
                    backgroundColor: '#E0E0E0',
                    borderRadius: '8px',
                    padding: '8px',
                    '&:hover': {
                      backgroundColor: '#D3D3D3',
                    },
                  }}
                  onClick={() => handleOpenDeleteDialog(request.event.event_id)}
                >
                  <DeleteIcon sx={{ color: 'red' }} />
                </IconButton>
              </Box>
            </TableCell>
          </TableRow>
        );
      })}
    </TableBody>
  </Table>
</TableContainer>

    </Box>
    
  );
};


