import Sidebar from './Sidebar';
import React, { useState, useEffect } from 'react';
import { Box, TextField, Typography, List, ListItem, ListItemText, Pagination } from '@mui/material';
import { useParams } from 'react-router-dom';

const ListRegis = () => {
  const { eventID } = useParams(); // Extract eventID from the URL
  const [registers, setRegisters] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredRegisters, setFilteredRegisters] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch registration data using eventID
    if (eventID) {
      fetch(`http://127.0.0.1:8000/api/registers/?event=${eventID}`)
        .then((response) => {
          if (!response.ok) {
            throw new Error('Failed to fetch registration data');
          }
          return response.json();
        })
        .then((data) => {
          setRegisters(data);
          setFilteredRegisters(data); // Initially, all registers are displayed
          setLoading(false);
        })
        .catch((error) => {
          console.error('Error fetching registration data:', error);
          setError('Failed to load registration data. Please try again later.');
          setLoading(false);
        });
    }
  }, [eventID]);

  // Handle search input change
  const handleSearch = (e) => {
    const query = e.target.value.toLowerCase();
    setSearchQuery(query);
    const filtered = registers.filter((reg) =>
      reg.name.toLowerCase().includes(query) // Adjust 'name' based on the registration object's structure
    );
    setFilteredRegisters(filtered);
  };

  return (
    <Box sx={{ display: 'flex', padding: 2 }}>
      {/* Sidebar */}
      <Sidebar />

      {/* Main content */}
      <Box sx={{ flex: 1, marginLeft: 2 }}>
        <Typography variant="h5">
          รายชื่อคนลงทะเบียน
        </Typography>

        <Box sx={{ marginY: 2 }}>
          <TextField
            fullWidth
            variant="outlined"
            placeholder="ค้นหา..."
            value={searchQuery}
            onChange={handleSearch}
          />
        </Box>

        {loading ? (
          <Typography>Loading...</Typography>
        ) : error ? (
          <Typography color="error">{error}</Typography>
        ) : (
          <List>
            {filteredRegisters.map((reg) => (
              <ListItem key={reg.id}>
                <ListItemText
                  primary={reg.name} // Adjust 'name' based on the registration object's structure
                  secondary={`Email: ${reg.email} | Phone: ${reg.phone}`} // Adjust fields based on the registration data
                />
              </ListItem>
            ))}
          </List>
        )}

        <Box sx={{ display: 'flex', justifyContent: 'center', marginTop: 2 }}>
          <Pagination
            count={Math.ceil(filteredRegisters.length / 10)} // Adjust count based on pagination logic
            variant="outlined"
            shape="rounded"
          />
        </Box>
      </Box>
    </Box>
  );
};

export default ListRegis;
