import { Box, Paper, Typography, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Button, Grid, Avatar, IconButton } from '@mui/material';
import { Description, Edit, Delete } from '@mui/icons-material';
import Sidebar from './Sidebar';
import React, { useState, useEffect } from 'react';

const Myactivity = ({ request_id }) => {
  const [activitiesApproved, setActivitiesApproved] = useState([]);

  useEffect(() => {
    if (request_id) {
      fetch(`/api/requests/approve/${request_id}/`)  // ใส่ request_id ใน URL
        .then((response) => {
          if (!response.ok) {
            throw new Error('Network response was not ok');
          }
          return response.json();
        })
        .then((data) => setActivitiesApproved(data))
        .catch((error) => console.error('Error fetching data:', error));
    }
  }, [request_id]);
  

  return (
    <Box sx={{ display: 'flex', padding: 2 }}>
    {/* Sidebar */}
    <Sidebar />

      <Box flexGrow={1} bgcolor="#f5f5f5" p={3}>
        <Paper elevation={2} sx={{ padding: 3 }}>

          {/* Approved Activities */}
          <Typography variant="h6" mt={3}>อนุมัติแล้ว</Typography>
           <Box p={3}>
        <TableContainer component={Paper}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>ชื่อกิจกรรม</TableCell>
                <TableCell>ประเภท</TableCell>
                <TableCell>วันจัดกิจกรรม</TableCell>
                <TableCell>จำนวนคนลงทะเบียน</TableCell>
                <TableCell>*</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {activitiesApproved.map((activity) => (
                <TableRow key={activity.event_id}>
                  <TableCell>{activity.event_name}</TableCell>
                  <TableCell>{activity.type}</TableCell>
                  <TableCell>{`${activity.startdate} - ${activity.enddate}`}</TableCell>
                  <TableCell>{activity.amount}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
    </Box>

          {/* Pending Activities */}
          <Typography variant="h6" mt={3}>รออนุมัติ</Typography>


          {/* Pagination */}
          <Box display="flex" justifyContent="center" mt={2}>
            <Button variant="outlined">1</Button>
            <Button variant="outlined">2</Button>
          </Box>
        </Paper>
      </Box>
    </Box>
  );
};

export default Myactivity;
