import {  MenuItem, Select, InputLabel, FormControl,TextField,Box, Paper, Typography, Button, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Grid, Dialog, DialogTitle, DialogContent } from '@mui/material';
import { Description, Edit, Delete } from '@mui/icons-material';
import Sidebar from './Sidebar';
import React, { useState, useEffect } from 'react';
import AddIcon from '@mui/icons-material/Add';

const Myactivity = () => {
  const [requests, setRequests] = useState([]);  // เก็บข้อมูลคำร้องทั้งหมด
  const [open, setOpen] = useState(false);
  const [eventTypes, setEventTypes] = useState([]); 
  const [formData, setFormData] = useState({
    event_name: '',
    type: '',
    address: '',
    province: '',
    detail: '',
    amount: '',
    startdate: '',
    enddate: '',
    timestart: '',
    contact: '',
  });
  const handleOpenForm = () => {
    setOpen(true); // เปิดฟอร์ม
  };

  const handleCloseForm = () => {
    setOpen(false); // ปิดฟอร์ม
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  
  const handleSubmit = () => {
    fetch('http://127.0.0.1:8000/api/events/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log('Event created:', data);
        handleCloseForm(); // ปิดฟอร์มหลังจากสร้างกิจกรรมสำเร็จ
      })
      .catch((error) => console.error('Error creating event:', error));
  };
  
  useEffect(() => {
    // ดึงข้อมูลคำร้องจาก API
    fetch('http://127.0.0.1:8000/api/requests/')
      .then((response) => response.json())
      .then((data) => {
        console.log('Requests :', data);  // ตรวจสอบข้อมูลคำร้อง
        setRequests(data);  // เก็บคำร้องใน state
      })
      .catch((error) => console.error('Error fetching requests:', error));
  }, []);


  return (
    <Box sx={{ display: 'flex', padding: 2 }}>
      {/* Sidebar */}
      <Sidebar />
      <Box flexGrow={1} bgcolor="#f5f5f5" p={3}>
        <Paper elevation={2} sx={{ padding: 3 }}>
          
               {/* Profile Section with Paper (raised box) */}
      <Paper elevation={3} sx={{ padding: 3, marginBottom: 4, borderRadius: '50'}}>
        <Typography variant="h5" gutterBottom>โปรไฟล์</Typography>
        <Grid container spacing={2}>
          <Grid item xs={12} md={6}>
            <Typography variant="body1">อาสาบ้านดินไทย</Typography>
            <Typography variant="body1">Tel: 369 258 147</Typography>
            <Typography variant="body1">Email: guyhawkins@gmail.com</Typography>
          </Grid>
          <Grid item xs={12} md={6} display="flex" justifyContent="flex-end">
          </Grid>
        </Grid>
      </Paper>
      <Button variant="contained" color="primary" onClick={handleOpenForm} startIcon={<AddIcon />}>เพิ่มกิจกรรม</Button>

{/* Form for Adding Event */}
<Dialog open={open} onClose={handleCloseForm} maxWidth="md" fullWidth>
      <DialogTitle>สร้างกิจกรรม</DialogTitle>
      <DialogContent>
        <Box sx={{ padding: 2 }}>
          <Grid container spacing={2}>
            {/* ชื่อกิจกรรม */}
            <Grid item xs={12} md={6}>
              <TextField
                label="ชื่อกิจกรรม"
                name="event_name"
                fullWidth
                onChange={handleChange}
                value={formData.event_name}
              />
            </Grid>

             {/* หมวดหมู่ */}
             <Grid item xs={12} md={6}>
              <FormControl fullWidth>
                <InputLabel>หมวดหมู่</InputLabel>
                <Select
                  label="หมวดหมู่"
                  name="type"
                  value={formData.type}
                  onChange={handleChange}
                >
                  {eventTypes.map((eventType) => (
                    <MenuItem key={eventType.value} value={eventType.value}>
                      {eventType.label}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>

            {/* สถานที่จัด */}
            <Grid item xs={12} md={6}>
              <TextField
                label="สถานที่จัด"
                name="address"
                fullWidth
                onChange={handleChange}
                value={formData.address}
              />
            </Grid>

            {/* จังหวัด */}
            <Grid item xs={12} md={6}>
              <TextField
                label="จังหวัด"
                name="province"
                fullWidth
                onChange={handleChange}
                value={formData.province}
              />
            </Grid>

            {/* รายละเอียด */}
            <Grid item xs={12}>
              <TextField
                label="รายละเอียด"
                name="detail"
                fullWidth
                multiline
                rows={4}
                onChange={handleChange}
                value={formData.detail}
              />
            </Grid>

            {/* จำนวนที่รับ */}
            <Grid item xs={12} md={6}>
              <TextField
                label="จำนวนที่รับ"
                name="amount"
                fullWidth
                onChange={handleChange}
                value={formData.amount}
              />
            </Grid>

            {/* วันที่เริ่มกิจกรรม */}
            <Grid item xs={12} md={6}>
              <TextField
                label="วันที่เริ่มกิจกรรม"
                name="startdate"
                type="date"
                fullWidth
                onChange={handleChange}
                value={formData.startdate}
                InputLabelProps={{
                  shrink: true,
                }}
              />
            </Grid>

            {/* วันสิ้นสุดกิจกรรม */}
            <Grid item xs={12} md={6}>
              <TextField
                label="วันสิ้นสุดกิจกรรม"
                name="enddate"
                type="date"
                fullWidth
                onChange={handleChange}
                value={formData.enddate}
                InputLabelProps={{
                  shrink: true,
                }}
              />
            </Grid>

            {/* เวลาจัดกิจกรรม */}
            <Grid item xs={12} md={6}>
              <TextField
                label="เวลาจัดกิจกรรม"
                name="timestart"
                type="time"
                fullWidth
                onChange={handleChange}
                value={formData.timestart}
                InputLabelProps={{
                  shrink: true,
                }}
              />
            </Grid>

            {/* ช่องทางการติดต่อ */}
            <Grid item xs={12} md={6}>
              <TextField
                label="ช่องทางการติดต่อ"
                name="contact"
                fullWidth
                onChange={handleChange}
                value={formData.contact}
              />
            </Grid>
          </Grid>

          <Box mt={3} display="flex" justifyContent="flex-end">
            <Button variant="contained" color="primary" onClick={handleSubmit}>
              สร้างกิจกรรม
            </Button>
          </Box>
        </Box>
      </DialogContent>
    </Dialog>

          {/* Approved Activities */}
          <Typography variant="h6" mt={3}>อนุมัติแล้ว</Typography>
          <Box p={3}>
            <TableContainer component={Paper}>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>ชื่อกิจกรรม</TableCell>
                    <TableCell>ประเภท</TableCell>
                    <TableCell>วันจัดกิจกรรม</TableCell>
                    <TableCell>จำนวนคนลงทะเบียน</TableCell>
                    <TableCell> * </TableCell> 
                  </TableRow>
                </TableHead>
                <TableBody>
                  {requests
                    .filter((request) => request.status === 'อนุมัติ') 
                    .map((request) => (
                      <TableRow key={request.request_id}>
                        <TableCell>{request.event.event_name}</TableCell>
                        <TableCell>{request.event.type}</TableCell>
                        <TableCell>{`${request.event.startdate} - ${request.event.enddate}`}</TableCell>
                        <TableCell>{request.event.amount}
                          <Typography component="span" sx={{ color: 'blue', cursor: 'pointer', marginLeft: 1 }}> รายชื่อ </Typography>
                        </TableCell>
                        <TableCell>
                          <Description style={{ cursor: 'pointer' }} />
                          <Edit style={{ cursor: 'pointer' }} />
                          <Delete style={{ cursor: 'pointer' }} />
                        </TableCell>
                      </TableRow>
                    ))}
                </TableBody>
              </Table>
            </TableContainer>
          </Box>
  
          {/* Pending Activities */}
          <Typography variant="h6" mt={3}>รออนุมัติ</Typography>
          <Box p={3}>
            <TableContainer component={Paper}>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>ชื่อกิจกรรม</TableCell>
                    <TableCell>ประเภท</TableCell>
                    <TableCell>วันจัดกิจกรรม</TableCell>
                    <TableCell>จำนวนคนลงทะเบียน</TableCell>
                    <TableCell> * </TableCell> 
                  </TableRow>
                </TableHead>
                <TableBody>
                  {requests
                    .filter((request) => request.status === 'รออนุมัติ')  
                    .map((request) => (
                      <TableRow key={request.request_id}>
                        <TableCell>{request.event.event_name}</TableCell>
                        <TableCell>{request.event.type}</TableCell>
                        <TableCell>{`${request.event.startdate} - ${request.event.enddate}`}</TableCell>
                        <TableCell>{request.event.amount}
                          <Typography component="span" sx={{ color: 'blue', cursor: 'pointer', marginLeft: 1 }}> รายชื่อ </Typography>
                        </TableCell>
                        <TableCell>
                          <Description style={{ cursor: 'pointer' }} />
                          <Edit style={{ cursor: 'pointer' }} />
                          <Delete style={{ cursor: 'pointer' }} />
                        </TableCell>
                      </TableRow>
                    ))}
                </TableBody>
              </Table>
            </TableContainer>
          </Box>
  
          {/* Rejected Activities */}
          <Typography variant="h6" mt={3}>ไม่อนุมัติ</Typography>
          <Box p={3}>
            <TableContainer component={Paper}>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>ชื่อกิจกรรม</TableCell>
                    <TableCell>ประเภท</TableCell>
                    <TableCell>วันจัดกิจกรรม</TableCell>
                    <TableCell>จำนวนคนลงทะเบียน</TableCell>
                    <TableCell> * </TableCell> 
                  </TableRow>
                </TableHead>
                <TableBody>
                  {requests
                    .filter((request) => request.status === 'ไม่อนุมัติ')  
                    .map((request) => (
                      <TableRow key={request.request_id}>
                        <TableCell>{request.event.event_name}</TableCell>
                        <TableCell>{request.event.type}</TableCell>
                        <TableCell>{`${request.event.startdate} - ${request.event.enddate}`}</TableCell>
                        <TableCell>{request.event.amount}
                          <Typography component="span" sx={{ color: 'blue', cursor: 'pointer', marginLeft: 1 }}> รายชื่อ </Typography>
                        </TableCell>
                        <TableCell>
                          <Description style={{ cursor: 'pointer' }} />
                          <Edit style={{ cursor: 'pointer' }} />
                          <Delete style={{ cursor: 'pointer' }} />
                        </TableCell>
                      </TableRow>
                    ))}
                </TableBody>
              </Table>
            </TableContainer>
          </Box>
        </Paper>
      </Box>
    </Box>
  );  
};

export default Myactivity;
