import Sidebar from './Sidebar';
import React, { useState, useEffect } from 'react';
import { Box, TextField, Typography, List, ListItem, ListItemText, MenuItem, Pagination } from '@mui/material';

const ListRegis = () => {
  const [registrations, setRegistrations] = useState([]);
  const [events, setEvents] = useState([]);
  const [selectedEventId, setSelectedEventId] = useState('');
  const [searchQuery, setSearchQuery] = useState('');

  // Fetch events on component mount
  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await fetch('http://127.0.0.1:8000/api/events/');
        const data = await response.json();
        setEvents(data);
      } catch (error) {
        console.error('Error fetching events:', error);
      }
    };

    fetchEvents();
  }, []);

  // Fetch registrations when an event is selected
  useEffect(() => {
    if (selectedEventId) {
      const fetchRegistrations = async () => {
        try {
          const response = await fetch(`http://127.0.0.1:8000/api/registers/?eventId=${selectedEventId}`);
          const data = await response.json();
          setRegistrations(data);
        } catch (error) {
          console.error('Error fetching registrations:', error);
        }
      };

      fetchRegistrations();
    }
  }, [selectedEventId]);

  // Handle event selection from search input or dropdown
  const handleEventSelect = (event) => {
    setSelectedEventId(event.target.value);
  };

  // Handle search input change
  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  return (
    <Box sx={{ display: 'flex', padding: 2 }}>
      {/* Sidebar */}
      <Sidebar />

      {/* Main content */}
      <Box sx={{ flex: 1, marginLeft: 2 }}>
        <Typography variant="h5" sx={{ backgroundColor: '#f39200', color: '#fff', padding: 1, borderRadius: 1, textAlign: 'center' }}>
          รายชื่อคนลงทะเบียน
        </Typography>

        <Box sx={{ marginY: 2 }}>
          <TextField
            fullWidth
            variant="outlined"
            placeholder="ค้นหา..."
            value={searchQuery}
            onChange={handleSearchChange}
          />
          <TextField
            fullWidth
            select
            label="เลือกกิจกรรม"
            value={selectedEventId}
            onChange={handleEventSelect}
            sx={{ marginY: 2 }}
          >
            {events.map((event) => (
              <MenuItem key={event.id} value={event.id}>
                {event.name}
              </MenuItem>
            ))}
          </TextField>
        </Box>

        <List>
          {registrations.map((registration) => (
            <ListItem key={registration.id}>
              <ListItemText primary={registration.name} secondary={registration.email} />
            </ListItem>
          ))}
        </List>

        <Box sx={{ display: 'flex', justifyContent: 'center', marginTop: 2 }}>
          <Pagination variant="outlined" shape="rounded" />
        </Box>
      </Box>
    </Box>
  );
};

export default ListRegis;
